%% Initializationclear ; close all; clc%%trying to get values to compute the estimte pricetryload('theta.mat');catchtheta = [0;0];endtryload('mu.mat');catchmu = [1];endtryload('sigma.mat');catchsigma = [1];end%%getting inputy = input ("Pick a number to estimate:");%calculate priceprice = estimatep(y, mu, sigma, theta);%%display pricefprintf('For mileage = %f km, we predict a price of %f dollars\n', y, ...    price);